export const Tree = function(value) {
  this.value = value;
  this.children = [];
};
Tree.prototype.constructor = Tree
Tree.prototype.addChild = function(value) {
    const newChild = new Tree(value);
    this.children.push(newChild);
};

Tree.prototype.contains = function(target) {
  let doesContain = false;
  function recurse(cur) {
    if (cur.value === target) {
      doesContain = true;
    } else {
      let current;
      for (let i=0;i < cur.children.length;i+=1) {
        if (cur.children[i].children !== []) {
          current = cur.children[i]
          recurse(current);
        }
      }
    }
  };
  recurse(this);
  if (doesContain === true) {
    return true;
  } else {
    return false;
  };
};
