/* eslint-disable */
import React, {useState, useEffect} from 'react';

// Striker Custom
import TreeClass from './Tree';
import OrgChartData from './SampleData';
import Child from './child';
// material-ui
import { Typography, Grid, List, ListItem, Card, Button } from '@material-ui/core';
import { Tree, TreeNode } from 'react-organizational-chart';
import { gridSpacing } from '../../../store/constant'

// project imports
import MainCard from 'ui-component/cards/MainCard';



// ==============================|| Page: Organization ||============================== //

// <TreeNode label={<div>Production</div>}/>
const Orgchart = () => {
    const orgTree = new TreeClass(OrgChartData.organization);
    OrgChartData.sections.forEach((section) => {
        if (section.parent === 0) {
            orgTree.addChild(section)
        } else {
            orgTree.getNodeById(section.parent).addChild(section)
        }
    })
    function recurse(cur) {
        if (cur.children.length !== 0) {
            return (
                <>
                <Child children={cur.children}/>
                {cur.children.map((child) => {
                    recurse(child)
                    return <Child children={child.children}/>
                })}
                </>
            )
        }
    }
    return (
        <Grid container spacing={gridSpacing}>
            <Grid item xs={12}>
                <Grid container spacing={gridSpacing}>
                    <Grid item xs={12} md={12}>
                    <MainCard title="Organization">
                    <Tree label={<div><h1>{OrgChartData.organization.name}</h1><h2>BAFB</h2></div>}>
                        {recurse(orgTree)}
                    </Tree>
                    </MainCard>  
                    </Grid>
                </Grid>
            </Grid>
        </Grid>
)};

export default Orgchart;
