/* eslint-disable */
import React, {useState, useEffect} from 'react';

// Striker Custom
import TreeClass from './Tree';
import OrgChartData from './SampleData';

// material-ui
import { Typography, Grid, List, ListItem, Card, Button } from '@material-ui/core';
import { Tree, TreeNode } from 'react-organizational-chart';
import { gridSpacing } from '../../../store/constant'

// project imports
import MainCard from 'ui-component/cards/MainCard';
import SimpleModal from '../users/list/Style1/AddUserModal';


// ==============================|| Page: Organization ||============================== //

// <TreeNode label={<div>Production</div>}/>
const Orgchart = () => {
    const [orgTree, setOrgTree] = useState();
    function createTree() {
        const myTree = new TreeClass(OrgChartData.organization);
        OrgChartData.sections.forEach((section, i) => {
        if (section.parent === 0) {
            myTree.addChild(section)
        } else {
            myTree.getNodeById(section.parent).addChild(section)
        }
        })
        return myTree
    }
    let newTree = createTree()
    useEffect(() => {

        console.log("orgTree", orgTree)
        console.log("newTree", newTree)
        setOrgTree(newTree);
    }, [])
    function recurse(cur) {
        if (typeof cur === "TreeClass") {
            console.log("im here")
            if (cur.children.length = 0) {
                console.log(cur.value.name)
                return (<TreeNode label={<div>{cur.value.name}</div>}/>)
            } else {
                for (let i=0;i < cur.children.length;i++) {
                    recurse(cur.children[i])
                    console.log(cur.children[i].value.name)
                    return (<TreeNode label={<div>{cur.children[i].value.name}</div>}/>)
                }
            }
        }
    }
    return (
        <Grid container spacing={gridSpacing}>
            <Grid item xs={12}>
                <Grid container spacing={gridSpacing}>
                    <Grid item xs={12} md={12}>
                    <MainCard title="Organization">
                    <Tree label={<div><h1>{OrgChartData.organization.name}</h1><h2>BAFB</h2></div>}>
                        {recurse(orgTree)}
                    </Tree>
                    </MainCard>  
                    </Grid>
                </Grid>
            </Grid>
        </Grid>
)};

export default Orgchart;
